# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 03_使用装饰器改进策略模式 # author: li zhang# date: 2019/12/3promos = []def promotion(promo_func):    promos.append(promo_func)    return promo_func@promotiondef fidelity(order):    return order.total() * .05 if order.customer.fidelity >= 1000 else 0@promotiondef bulk_item(order):    """单个商品为20个或以上时提供10%折扣"""    discount = 0    for item in order.cart:        if item.quantity >= 20:            discount += item.total() * .1    return discount@promotiondef large_order(order):    """订单中的不同商品达到10个或以上时提供7%折扣"""    distinct_items = {item.product for item in order.cart}    if len(distinct_items) >= 10:        return order.total() * .07    return 0