# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 04_变量作用域和闭包 # author: li zhang# date: 2019/12/3from dis import disb = 6# def f2(a):#     print(a)#     print(b)#     b = 9# print(dis(f2))def f3(a):    global b    print(a)    print(b)    b = 9print(dis(f3))# python 不要求声明变量，但是假定在函数定义体中声明的变量是局部变量# 闭包是指延伸了作用域的函数def make_average():    series = []    def average(new_value):        series.append(new_value)        total = sum(series)        return total / len(series)    return averageavg = make_average()print(avg(10))print(avg.__code__.co_varnames)print(avg.__code__.co_freevars)