# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 01_用户定义序列 # author: li zhang# date: 2019/11/7# 新知识是抽象基类的严格规定和类型检查# 抽象基类是什么东西# 抽象基类如何检查具体子类是否符合接口定义# 如何使用注册机制生命一个类实现接口class Vector2d:    typecode = 'd'    def __init__(self, x, y):        self.__x = float(x)        self.__y = float(y)    def x(self):        return self.__x    @property  # 将一个方法编程属性进行调用    def y(self):        return self.__y    def __inter__(self):        return (i for i in (self.x, self.y))st = [4,4,2,3,1]print(st[:3])print(st[-2:])