# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 01_多重继承 # author: li zhang# date: 2019/11/18class A(object):    def ping(self):        print('A ping', self)class B(A):    def pong(self):        print('B pong', self)class C(A):    def pong(self):        print('C PONG', self)class D(B, C):    def ping(self):        super().ping()        print('D post-ping', self)    def ping_pong(self):        self.ping()        super().ping()        self.pong()        super().pong()        C.pong(self)d = D()d.ping_pong()print(D.__mro__)# 通常会使用 __mro__ 分析继承的顺序# 多重继承的真实应用# 多重继承显然不是灵丹妙药import tkinter