# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 04_迭代器惰性实现 # author: li zhang# date: 2019/11/25import reimport reprlibRE_WORD = re.compile('\w+') # w匹配非特殊字符，+ 匹配一次或者多次print(RE_WORD)class Sentence(object):    def __init__(self, text):        self.text = text    def __repr__(self):  # 转换成供解释器可以解释的格式        return 'Sentence(%s)' % reprlib.repr(self.text)    def __iter__(self):        for match in RE_WORD.finditer(self.text):            yield match.group()