# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 05_生成器表达式 # author: li zhang# date: 2019/11/25# 生成器表达式就是制造生成器的工厂def gen_AB():    print('start')    yield 'A'    print('continue')    yield 'B'    print('end.')res1 = [x*3 for x in gen_AB()] # 列表推导for i in res1:    print('--->', i)res2 = (x*3 for x in gen_AB())print(res2)for i in res2:    print(i)# 使用itertools模块生成等差数列# itertools提供了9个生成器函数import itertoolsgen = itertools.count(1, .5)print(next(gen))print(next(gen))gen = itertools.takewhile(lambda x:x<3, itertools.count(1, 0.5))print(list(gen))