# !/usr/bin/env python# -*- coding:utf-8 -*-# name: 04_协程返回值 # author: li zhang# date: 2019/12/4from collections import namedtupleResult = namedtuple('Result', 'count average')print(Result)def average():    total = 0.0    count = 0    average = None    while True:        term = yield        if term is None:            break        total += term        count += 1        average = total/count    return Result(count, average)cor = average()next(cor)cor.send(10)cor.send(30)cor.send(6.5)cor.send(None)